01619 bool XDATETIME::GetDateTimeToStringISO8601(XDWORD modificator, XSTRING& string)
01620 {
01621   string.Empty();
01622   
01623   XSTRING datestr;
01624   XSTRING timestr;
01625   XCHAR   dateseparator = __C('-');
01626   XCHAR   timeseparator = __C(':');
01627 
01628   if(!islocal) 
01629     {
01630       modificator |= XDATETIME_FORMAT_ISO8601_UTC;
01631     }
01632 
01633   if(IsModificatorActive(modificator, XDATETIME_FORMAT_ISO8601_BASIC))
01634     {
01635       datestr.Format(__L("%04d%02d%02d"), year, month, day);
01636     
01637       if(IsModificatorActive(modificator, XDATETIME_FORMAT_TIMEWITHSECONDS))
01638         {
01639           timestr.Format(__L("%02d%02d%02d"), hours, minutes, seconds);
01640         }
01641        else  
01642         {
01643           timestr.Format(__L("%02d%02d"), hours, minutes);
01644         }
01645     }
01646    else
01647     {
01648       datestr.Format(__L("%04d%c%02d%c%02d") , year, dateseparator, month, dateseparator, day);
01649     
01650       if(IsModificatorActive(modificator, XDATETIME_FORMAT_TIMEWITHSECONDS))
01651         {
01652           timestr.Format(__L("%02d%c%02d%c%02d"), hours, timeseparator, minutes, timeseparator , seconds);
01653         }
01654        else  
01655         {
01656           timestr.Format(__L("%02d%c%02d"), hours, timeseparator, minutes, timeseparator);
01657         }
01658     }
01659 
01660   if(IsModificatorActive(modificator, XDATETIME_FORMAT_TIMEWITHMILLISECONDS))
01661     {
01662       timestr.AddFormat(__L(".%03d"), milliseconds);
01663     }  
01664 
01665   if(IsModificatorActive(modificator, XDATETIME_FORMAT_ADDDATE))
01666     {
01667       string += datestr;
01668     }
01669 
01670   if(IsModificatorActive(modificator, XDATETIME_FORMAT_ADDTIME))
01671     {
01672       if(!string.IsEmpty()) 
01673         {
01674           if(IsModificatorActive(modificator, XDATETIME_FORMAT_ISO8601_UTC))
01675             {
01676               string += __L("T");
01677             }
01678            else    
01679             {
01680               string += __L(" ");
01681             }
01682         }
01683 
01684       string += timestr;
01685     }
01686 
01687   if(IsModificatorActive(modificator, XDATETIME_FORMAT_ISO8601_UTC))
01688     {
01689       if(IsModificatorActive(modificator, XDATETIME_FORMAT_ISO8601_ADDHOUROFFSET))
01690         {
01691           int meridiandiff  = GetMeridianDifference();
01692 
01693           if(!meridiandiff)
01694             {
01695               string.Add(__L("Z"));
01696             }
01697            else
01698             {
01699               XDATETIME*  datetime = GEN_XFACTORY.CreateDateTime();
01700               if(datetime)
01701                 {
01702                   XSTRING meridiandiffstr;  
01703                   int     bias         = 0;
01704                   
01705                   datetime->IsDayLigthSavingTime(&bias);
01706                   
01707                   datetime->SetToZero();
01708 
01709                   datetime->SetIsLocal(true);
01710 
01711                   datetime->SetDay(1);
01712                   datetime->SetMonth(1);
01713                   datetime->SetYear(1);
01714 
01715                   datetime->AddMinutes(abs(meridiandiff) + abs(bias));
01716               
01717                   datetime->GetDateTimeToString(XDATETIME_FORMAT_ADDTIME, meridiandiffstr);
01718 
01719                   string.AddFormat(__L("%c%s"), (meridiandiff>0)?__C('+'):__C('-'), meridiandiffstr.Get());
01720 
01721                   GEN_XFACTORY.DeleteDateTime(datetime);
01722                 }
01723             }
01724         }
01725        else
01726         {
01727           string.Add(__L("Z"));
01728         }
01729     }
01730      
01731   return IsValidDate(); 
01732 }
