01747 bool XDATETIME::GetDateTimeFromString(XSTRING& string, XWORD modificator)
01748 {
01749   int           index        = 0;
01750   XVECTOR<int>  typeblock;
01751   int           nblock       = 0;
01752   int           startindex   = 0;
01753   XCHAR         separator    = ((IsModificatorActive(modificator, XDATETIME_FORMAT_DATEWITHDASH))?__C('-'):__C('/'));
01754   XSTRING       validcharacters;
01755 
01756   validcharacters  = __L("0123456789 :.");
01757   validcharacters += separator;
01758 
01759   if(!string.AreValidCharacters(validcharacters.Get())) return false;
01760 
01761   if((IsModificatorActive(modificator, XDATETIME_FORMAT_ADDDAYOFWEEK)) && (IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTDAYOFWEEK)))    
01762     {
01763       typeblock.Add(1);
01764     }
01765 
01766   if((IsModificatorActive(modificator, XDATETIME_FORMAT_ADDTIME)) && (IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTTIME)))         
01767     {
01768       typeblock.Add(2);
01769     }
01770 
01771   if(IsModificatorActive(modificator, XDATETIME_FORMAT_ADDDATE))                                                                                  
01772     {
01773       typeblock.Add(3);
01774     }
01775 
01776   if((IsModificatorActive(modificator, XDATETIME_FORMAT_ADDTIME)) && (!(IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTTIME))))      
01777     {
01778       typeblock.Add(2);
01779     }
01780 
01781   if((IsModificatorActive(modificator, XDATETIME_FORMAT_ADDDAYOFWEEK)) && (!(IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTDAYOFWEEK)))) 
01782     {
01783       typeblock.Add(1);
01784     }
01785 
01786   while(index<=(int)string.GetSize())
01787     {
01788       XCHAR character = string.Get()[index];
01789 
01790       if((character == 0x20) || (index==(int)string.GetSize()))
01791         {
01792           XSTRING partial;
01793 
01794           if(index<(int)string.GetSize())
01795             {
01796               string.Copy(startindex, index, partial);
01797             }
01798            else 
01799             {
01800               string.Copy(startindex, partial);
01801             }
01802 
01803           if(!partial.IsEmpty())
01804             {
01805               switch(typeblock.Get(nblock))
01806                 {
01807                   case 1: break; // Day of Week not used (can be calculated).
01808 
01809                   case 2: if(IsModificatorActive(modificator, XDATETIME_FORMAT_TIMEWITHMILLISECONDS))
01810                             {
01811                               if(IsModificatorActive(modificator, XDATETIME_FORMAT_TIMEWITHSECONDS))
01812                                 {
01813                                   partial.UnFormat(__L("%d:%d:%d.%d"), &hours ,&minutes ,&seconds, &milliseconds);
01814                                 }
01815                                else  
01816                                 {
01817                                   partial.UnFormat(__L("%d:%d:%d"), &hours ,&minutes, &milliseconds);
01818                                 }
01819                             }
01820                            else
01821                             {
01822                               if(IsModificatorActive(modificator, XDATETIME_FORMAT_TIMEWITHSECONDS))
01823                                 {
01824                                   partial.UnFormat(__L("%d:%d:%d"), &hours ,&minutes ,&seconds);
01825                                 }
01826                                else  
01827                                 {
01828                                   partial.UnFormat(__L("%d:%d"), &hours ,&minutes);
01829                                 }
01830                             }
01831                           break;
01832 
01833                   case 3: if(separator == __C('-'))
01834                             {
01835                               if(IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTDATEDAY)) 
01836                                 {
01837                                   partial.UnFormat(__L("%d-%d-%d"), &day, &month, &year);
01838                                 }
01839                                else 
01840                                 {
01841                                   if(IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTDATEMONTH))
01842                                     {
01843                                       partial.UnFormat(__L("%d-%d-%d"), &month, &day, &year);
01844                                     }
01845                                    else 
01846                                     {
01847                                       if(IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTDATEYEAR))
01848                                         {
01849                                           partial.UnFormat(__L("%d-%d-%d"), &year, &month, &day);
01850                                         }
01851                                        else 
01852                                         {
01853                                           partial.UnFormat(__L("%d-%d-%d"), &day, &month, &year);
01854                                         }
01855                                     }
01856                                 }
01857                             }
01858 
01859                           if(separator == __C('/'))
01860                             {
01861                               if(IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTDATEDAY))
01862                                 {
01863                                   partial.UnFormat(__L("%d/%d/%d"), &day, &month, &year);
01864                                 }
01865                                else 
01866                                 {
01867                                   if(IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTDATEMONTH))
01868                                     {
01869                                       partial.UnFormat(__L("%d/%d/%d"), &month, &day, &year);
01870                                     }
01871                                    else 
01872                                     {
01873                                       if(IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTDATEYEAR))
01874                                         {
01875                                           partial.UnFormat(__L("%d/%d/%d"), &year, &month, &day);
01876                                         }
01877                                        else 
01878                                         {
01879                                           partial.UnFormat(__L("%d/%d/%d"), &day, &month, &year);
01880                                         }
01881                                     }
01882                                 }
01883                             }
01884 
01885                           break;
01886                 }
01887 
01888               nblock++;
01889             }
01890 
01891           startindex = index+1;
01892         }
01893 
01894       index++;
01895     }
01896 
01897   typeblock.DeleteAll();
01898 
01899   return IsValidDate();
01900 }
