02119 
02129 int XDATETIME::GetDayOfYear() 
02130 {
02131   int dayspermonth[] = {0, 31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31};
02132   int dayofyear = 0;
02133   int i;
02134 
02135   // Check for leap year and update the number of days in February
02136   if((year % 4 == 0 && year % 100 != 0) || (year % 400 == 0)) 
02137     {
02138       dayspermonth[2] = 29;
02139     }
02140 
02141   // Sum the days of the previous months
02142   for(i = 1; i < month; i++) 
02143     {
02144       dayofyear += dayspermonth[i];
02145     }
02146 
02147   // Add the current day
02148   dayofyear += day;
02149 
02150   return dayofyear;
02151 }
02152 
02153 
02163 int XDATETIME::GetWeekOfYear() 
02164 {
02165   int dayofyear   = XDATETIME::GetDayOfYear();
02166   int daysperweek = 7;
02167   int weekofyear  = 0;
02168     
02169   // Calculate the week of the year
02170   weekofyear = (dayofyear - 1) / daysperweek + 1;
02171 
02172   return weekofyear;
02173 }
