02185 XQWORD XDATETIME::GetEPOCHFormat()
02186 {
02187   Read();
02188 
02189   XDATETIME origin;
02190 
02191   origin.SetToZero();
02192   origin.SetDay(1);
02193   origin.SetMonth(1);
02194   origin.SetYear(1970);
02195 
02196   XQWORD seconds_origin = origin.GetSeconsFromDate(false);
02197   XQWORD seconds_actual = GetSeconsFromDate(false);
02198 
02199   return (seconds_actual - seconds_origin);
02200 }
