02313 int XDATETIME::Compare(XDATETIME& datetime)
02314 {
02315   if(datetime.GetYear() > GetYear()) 
02316     {
02317       return  1;
02318     }
02319 
02320   if(datetime.GetYear() < GetYear()) 
02321     {
02322       return -1;
02323     }
02324 
02325   if(datetime.GetMonth() > GetMonth()) 
02326     {
02327       return  1;
02328     }
02329 
02330   if(datetime.GetMonth() < GetMonth()) 
02331     {
02332       return -1;
02333     }
02334 
02335   if(datetime.GetDay() > GetDay()) 
02336     {
02337       return  1;
02338     }
02339 
02340   if(datetime.GetDay() < GetDay()) 
02341     {
02342       return -1;
02343     }
02344 
02345   if(datetime.GetHours() > GetHours()) 
02346     {
02347       return  1;
02348     }
02349 
02350   if(datetime.GetHours() < GetHours()) 
02351     {
02352       return -1;
02353     }
02354 
02355   if(datetime.GetMinutes() > GetMinutes()) 
02356     {
02357       return  1;
02358     }
02359 
02360   if(datetime.GetMinutes() < GetMinutes()) 
02361     {
02362       return -1;
02363     }
02364 
02365   if(datetime.GetSeconds() > GetSeconds()) 
02366     {
02367       return  1;
02368     }
02369 
02370   if(datetime.GetSeconds() < GetSeconds()) 
02371     {
02372       return -1;
02373     }
02374 
02375   if(datetime.GetMilliSeconds() > GetMilliSeconds()) 
02376     {
02377       return  1;
02378     }
02379 
02380   if(datetime.GetMilliSeconds() < GetMilliSeconds()) 
02381     {
02382       return -1;
02383     }
02384 
02385   return 0;
02386 }
