02401 bool XDATETIME::IsModificatorActive(XDWORD modificator, XDWORD tocheck)
02402 {
02403   if((XDWORD)(modificator & tocheck) == tocheck)
02404     {
02405       return true;
02406     }
02407 
02408   return false;
02409 }
