00620 bool XDRIVEIMAGEMANAGER::ReadToIMGFile(XDRIVEIMAGEMANAGER_DRIVE& drive, XPATH& pathfileIMG, XQWORD nsectorsperblock)
00621 {
00622   XFILE*   xfileIMG;
00623   XBUFFER  readbuffer;
00624   XQWORD   nsectorstoread = nsectorsperblock;
00625   XQWORD   nsectorsread   = 0;
00626   bool     status         = 0;
00627 
00628   if(!drive.GetSize()) return status;
00629 
00630   GEN_XFACTORY_CREATE(xfileIMG, Create_File())
00631   if(xfileIMG)
00632     {
00633       if(xfileIMG->Create(pathfileIMG))
00634         {
00635           XQWORD indexsector=0;
00636 
00637           while(indexsector<drive.GetNumberSectors())
00638             {
00639               if((indexsector + nsectorstoread) > drive.GetNumberSectors()) nsectorstoread = (drive.GetNumberSectors() - indexsector);
00640 
00641               XDRIVEIMAGEMANAGER_XEVENT xevent(this, XDRIVEIMAGEMANAGER_XEVENT_TYPE_READTOIMGFILE);
00642 
00643               xevent.SetDrive(&drive);
00644               xevent.SetIndexSector(indexsector);
00645               xevent.SetNumberSectorsInOperation(nsectorstoread);
00646               xevent.SetError(XDRIVEIMAGEMANAGER_ERROR_NONE);
00647 
00648               status = Read(drive, indexsector, nsectorstoread, readbuffer);
00649               if(!status) xevent.SetError(XDRIVEIMAGEMANAGER_ERROR_READSECTORS);
00650 
00651               if(status)
00652                 {
00653                   status = xfileIMG->Write(readbuffer);
00654                   if(!status) xevent.SetError(XDRIVEIMAGEMANAGER_ERROR_WRITEIMGFILE);
00655                 }
00656 
00657               if(status) nsectorsread += nsectorstoread;
00658 
00659               lastoperationpercent = (((float)nsectorsread*100)/(float)drive.GetNumberSectors());
00660 
00661               xevent.SetError(lasterror);
00662               xevent.SetOperationPercent(lastoperationpercent);
00663 
00664               PostEvent(&xevent);
00665 
00666               if(!status) break;
00667 
00668               indexsector += nsectorstoread;
00669             }
00670 
00671           xfileIMG->Close();
00672         }
00673 
00674       GEN_XFACTORY.Delete_File(xfileIMG);
00675     }
00676 
