00328 bool XFILE::EraseBlock(XQWORD size)
00329 {
00330   XBYTE* tbuffer;
00331   XQWORD position;
00332   XQWORD sizetomove;
00333   XDWORD nblocks;
00334   XDWORD rest;
00335   bool   status = true;
00336 
00337   if(!isopen) return false;
00338 
00339   if(!GetPosition(position)) return false;
00340 
00341   sizetomove = GetSize() - position - size;
00342 
00343   tbuffer= new XBYTE [XFILE_MAXBUFFER];
00344   if(!tbuffer) return false;
00345 
00346   nblocks = (XDWORD)(sizetomove / XFILE_MAXBUFFER);
00347   rest    = (XDWORD)(sizetomove % XFILE_MAXBUFFER);
00348 
00349   for(XDWORD c=0; c<nblocks; c++)
00350     {
00351       SetPosition(position + size + ((XQWORD)c * XFILE_MAXBUFFER));
00352       if(Read(tbuffer, XFILE_MAXBUFFER))
00353         {
00354           SetPosition(position + ((XQWORD)c * XFILE_MAXBUFFER));
00355           Write(tbuffer, XFILE_MAXBUFFER);
00356         }
00357     }
00358 
00359   SetPosition(position + size +((XQWORD)nblocks * XFILE_MAXBUFFER));
00360   if(Read(tbuffer, rest))
00361     {
00362       SetPosition(position + ((XQWORD)nblocks * XFILE_MAXBUFFER));
00363       Write(tbuffer, rest);
00364     }
00365 
00366   SetSize(GetSize() - size);
00367 
00368   delete [] tbuffer;
00369 
00370   return status;
00371 }
