00422        else
00423         {
00424           rb = XFILE_MAXBUFFER;
00425         }
00426 
00427       SetPosition(position);
00428       Read(movebuffer, &rb);
00429 
00430       SetPosition(position+size);
00431       Write(movebuffer,rb);
00432 
00433       position -= XFILE_MAXBUFFER;
00434     }
00435 
00436   SetPosition(origin);
00437   Write(buffer,size);
00438 
00439   if(status==true) ActualizeSize();
