00900 XVARIANT* XFILECFG::GetValue(XCHAR* group, XCHAR* ID)
00901 {
00902   XVARIANT* value = NULL;
00903 
00904   if((!group) || (!ID))
00905     {
00906       return NULL;
00907     }
00908 
00909   for(XDWORD c=0; c<values.GetSize(); c++)
00910     {
00911       XFILECFGVALUE* cfgvalue = values.Get(c);
00912       if(cfgvalue)
00913         {
00914           if((!cfgvalue->GetGroup()->Compare(group, true)) && (!cfgvalue->GetID()->Compare(ID, true)))
00915             {
00916               value = new XVARIANT();
00917               if(value)
00918                 {
00919                   switch(cfgvalue->GetType())
00920                     {
00921                       case XFILECFG_VALUETYPE_UNKNOWN :
00922                                              default  : delete value; 
00923                                                         value = NULL;
00924                                                         break;
00925 
00926                       case XFILECFG_VALUETYPE_INT     : (*value) = (*(int*)(cfgvalue->GetValue()));     break; 
00927                       case XFILECFG_VALUETYPE_MASK    : (*value) = (*(XWORD*)(cfgvalue->GetValue()));   break;
00928                       case XFILECFG_VALUETYPE_FLOAT   : (*value) = (*(float*)(cfgvalue->GetValue()));   break;                                                        
00929 
00930                       case XFILECFG_VALUETYPE_STRING  : { XSTRING* _value = (XSTRING*)(cfgvalue->GetValue());                                                        
00931                                                           (*value) = _value->Get(); 
00932                                                         }
00933                                                         break;
00934 
00935                       case XFILECFG_VALUETYPE_BOOLEAN : (*value) = (*(bool*)(cfgvalue->GetValue()));    break; 
00936                     }
00937                 }    
00938             }
00939         }
00940     }
00941   
00942   return value;
00943 }
