01381 bool XFILECFG::GenerateKeySecuence(XCHAR* IDbase, XCHAR* mask, int index, XSTRING& key)
01382 {
01383   XSTRING keymask;      
01384   
01385   key.Empty();     
01386   
01387   if(!IDbase) return false;
01388     
01389   if(mask)  
01390     {
01391       keymask.Format(__L("%s%s"), IDbase, mask);
01392       key.Format(keymask.Get(), index);
01393 
01394     } else key.Format(__L("%s%d"), IDbase, index);
01395   
01396   return true;
01397 }
