00745 bool XFILECSV::CreateRecordOfLine(XSTRING& line, XFILECSV_RECORD* record)
00746 {
00747   if(line.IsEmpty()) return false;
00748 
00749   int start = 0;
00750   int end   = 0;
00751 
00752   do{ end = line.Find(__L(";"),true,start);
00753       if(end == XSTRING_NOTFOUND)
00754         {
00755           end = line.Find(__L(","),true,start);
00756           if(end != XSTRING_NOTFOUND) SetSeparator(__C(','));
00757 
00758         } else SetSeparator(__C(';'));
00759 
00760       XSTRING element;
00761 
00762       if(end != XSTRING_NOTFOUND)
00763         {
00764           line.Copy(start,end,element);
00765 
00766           record->AddElement(element);
00767 
00768           start = end+1;
00769         }
00770        else
00771         {
00772           line.Copy(start,element);
00773           record->AddElement(element);
00774 
00775           break;
00776         }
00777 
00778     } while(1);
00779 
00780   return true;
