00573 bool XFILEDBF_HEADER::Load(XFILE* xfile)
00574 {
00575   // ---------------------------
00576   // Read header
00577   if(!xfile->SetPosition(8)) return false;
00578 
00579   if(!xfile->Read((XBYTE*)&headersize,2)) return false;
00580   if(!headersize) return 0;
00581 
00582   if(headerdata)
00583     {
00584       delete[] headerdata;
00585       headerdata = NULL;
00586     }
00587 
00588   headerdata = new XBYTE[headersize];
00589   if(!headerdata) return false;
00590 
00591   if(!xfile->SetPosition(0)) return false;
00592   if(!xfile->Read(headerdata,headersize)) return false;
00593 
00594   int index=XFILEDBF_FIELDSTART;
00595 
00596   while(headerdata[index]!=0x0D)
00597     {
00598       XFILEDBF_FIELDTYPE type;
00599 
00600       switch(headerdata[index+11])
00601         {
00602           case 'C' : type=XFILEDBF_FIELDTYPE_CHARACTER; break;
00603           case 'D' : type=XFILEDBF_FIELDTYPE_DATE;      break;
00604           case 'F' : type=XFILEDBF_FIELDTYPE_FLOATING;  break;
00605           case 'N' : type=XFILEDBF_FIELDTYPE_BINARYHEX; break;
00606           case 'L' : type=XFILEDBF_FIELDTYPE_LOGICAL;   break;
00607           case 'M' : type=XFILEDBF_FIELDTYPE_MEMO;      break;
00608           case 'V' : type=XFILEDBF_FIELDTYPE_VARIABLE;  break;
00609           case 'P' : type=XFILEDBF_FIELDTYPE_PICTURE;   break;
00610           case 'B' : type=XFILEDBF_FIELDTYPE_BINARY;    break;
00611           case 'G' : type=XFILEDBF_FIELDTYPE_GENERAL;   break;
00612           case '2' : type=XFILEDBF_FIELDTYPE_SHORTINT;  break;
00613           case '4' : type=XFILEDBF_FIELDTYPE_LONGINT;   break;
00614           case '8' : type=XFILEDBF_FIELDTYPE_DOUBLE;    break;
00615           default  : type=XFILEDBF_FIELDTYPE_UNKNOWN;   break;
00616         }
00617 
00618       if(type!=XFILEDBF_FIELDTYPE_UNKNOWN)
00619         {
00620           XSTRING name((char*)&headerdata[index]);
00621 
00622           if(!CreateField(type, name.Get(), headerdata[index+16])) return false;
00623         }
00624 
00625       index+=XFILEDBF_FIELDSIZE;
00626     }
00627 
00628   return true;
00629 }
