01327 XFILEDBF_RECORD* XFILEDBF::ReadRecord(XDWORD nrecord)
01328 {
01329   if(!IsOpen()) return NULL;
01330   if(!header)   return NULL;
01331 
01332   XFILEDBF_RECORD* record;
01333   if(nrecord>=header->GetNRecords()) return NULL;
01334 
01335   XDWORD sizerecord = header->GetSizeRecord();
01336 
01337   if(!sizerecord) return NULL;
01338 
01339   record = new XFILEDBF_RECORD(header->GetFields());
01340   if(!record) return NULL;
01341 
01342   XBYTE* data = new XBYTE [sizerecord];
01343   if(!data)
01344     {
01345       delete record;
01346       return NULL;
01347     }
01348 
01349   int position=header->GetHeaderSize()+(sizerecord*nrecord);
01350 
01351   file->SetPosition(position);
01352 
01353   if(!file->Read(data,sizerecord))
01354     {
01355       delete record;
01356       delete[] data;
01357 
01358       return NULL;
01359     }
01360 
01361   record->SetData(data,sizerecord);
01362 
01363   delete[] data;
01364 
01365   return record;
01366 }
