01381 bool XFILEDBF::WriteRecord(XDWORD nrecord,XFILEDBF_RECORD* record)
01382 {
01383   if(!IsOpen()) return false;
01384   if(!header)   return false;
01385 
01386   int position = header->GetHeaderSize()+(header->GetSizeRecord()*nrecord);
01387 
01388   file->SetPosition(position);
01389 
01390   XBYTE delflag=record->IsDelete()?'*':' ';
01391 
01392   if(!file->Write(&delflag,1)) return false;
01393   if(!file->Write(record->GetData(0),header->GetSizeRecord()-1)) return false;
01394 
01395   return true;
