01411 bool XFILEDBF::InsertRecord(XDWORD nrecord, XFILEDBF_RECORD* record)
01412 {
01413   if(!IsOpen()) return false;
01414   if(!header)   return false;
01415 
01416   int position = header->GetHeaderSize()+(header->GetSizeRecord()*nrecord);
01417 
01418   file->SetPosition(position);
01419 
01420   XBYTE delflag=record->IsDelete()?'*':' ';
01421 
01422   XBUFFER xbuffer;
01423 
01424   xbuffer.Add(delflag);
01425   xbuffer.Add(record->GetData(0), (header->GetSizeRecord()-1));
01426 
01427   if(!file->InsertBlock(xbuffer.Get(), xbuffer.GetSize()))   return false;
01428 
01429   header->SetNRecords(header->GetNRecords()+1);
01430   header->Update(file);
01431 
01432   return true;
01433 }
