01447 bool XFILEDBF::AddRecord(XFILEDBF_RECORD* record)
01448 {
01449   if(!IsOpen()) return false;
01450   if(!header)   return false;
01451 
01452   XDWORD size = header->GetSizeRecord()-1;
01453 
01454   XBYTE   specialend;
01455   XQWORD  position;
01456 
01457   file->SetPosition((XDWORD)XFILE_SEEKEND);
01458   file->GetPosition(position);
01459   file->SetPosition(position-1);
01460 
01461   if(file->Read(&specialend,1))
01462     {
01463       if(specialend == 0x1A) file->SetPosition(position-1);
01464     }
01465 
01466   XBYTE delflag=record->IsDelete()?'*':' ';
01467 
01468   if(!file->Write(&delflag,1)) return false;
01469   if(!file->Write(record->GetData(0),size)) return false;
01470 
01471   specialend = 0x1A;
01472   if(!file->Write(&specialend, 1)) return false;
01473 
01474   header->SetNRecords(header->GetNRecords()+1);
01475 
01476   header->Update(file);
01477 
01478   return true;
01479 }
