01493 bool XFILEDBF::DeleteRecord(XDWORD nrecord)
01494 {
01495   if(!IsOpen()) return false;
01496   if(!header)   return false;
01497 
01498   char*  data;
01499   int    size = header->GetSizeRecord()-1;
01500 
01501   data = new char[size+10];
01502   if(!data) return false;
01503 
01504   memset(data,0,size+10);
01505 
01506   int position = header->GetHeaderSize()+(header->GetSizeRecord()*nrecord);
01507 
01508   file->SetPosition(position);
01509 
01510   XBYTE delflag='*';
01511 
01512   if(!file->Write(&delflag,1)) return false;
01513   if(!file->Write((XBYTE*)data,size)) return false;
01514 
01515   delete[] data;
01516 
01517   return true;
01518 }
