01530 bool XFILEDBF::Pack()
01531 {
01532   if(!IsOpen()) return false;
01533   if(!header)   return false;
01534 
01535   XPATH xpathtmp;
01536 
01537   xpathtmp  = GetXPath()->Get();
01538   xpathtmp += __L(".tmp");
01539 
01540   XFILEDBF* dbftmp = new XFILEDBF();
01541   if(!dbftmp) return false;
01542 
01543   dbftmp->GetHeader()->CopyFrom(header);
01544   dbftmp->GetHeader()->SetNRecords(0);
01545 
01546   delete dbftmp->GetHeader()->GetHeaderData();
01547 
01548   if(!dbftmp->Create(xpathtmp))
01549     {
01550       delete dbftmp;
01551       return false;
01552     }
01553 
01554   for(XDWORD c=0; c< header->GetNRecords(); c++)
01555     {
01556       XFILEDBF_RECORD* record = ReadRecord(c);
01557       if(record)
01558         {
01559           if(!record->IsDelete())
01560             {
01561               dbftmp->AddRecord(record);
01562             }
01563 
01564           delete record;
01565         }
01566     }
01567 
01568   dbftmp->Close();
01569 
01570   delete dbftmp;
01571 
01572   Close();
01573 
01574 
01575   XFILE* GEN_XFACTORY_CREATE(xfile, Create_File())
01576   if(!xfile) return false;
01577 
01578   bool status = true;
01579 
01580   if(xfile->Erase(GetXPath()->Get()))
01581     {
01582       if(!xfile->Rename(xpathtmp.Get(), GetXPath()->Get())) status = false;
01583 
01584     } else status = false;
01585 
01586   GEN_XFACTORY.Delete_File(xfile);
01587 
01588   if(status)
01589     {
01590       status = Open((*GetXPath()), GetPrimaryFile()->IsReadOnly());
01591       if(status)
01592         {
01593           delete GetHeader();
01594           CreateHeader();
01595           GetHeader()->Load(GetPrimaryFile());
01596         }
01597     }
01598 
01599   return status;
01600 }
