01615 bool XFILEDBF::Reduction(int maxrecords, int reductionpercent)
01616 {
01617   if(!IsOpen()) return false;
01618   if(!header)   return false;
01619 
01620   int nrecords  = header->GetNRecords();
01621   if(nrecords<maxrecords) return true;
01622 
01623   int nrectosave = (nrecords*reductionpercent)/100;
01624 
01625   for(int c=0;c<(nrecords-nrectosave);c++)
01626     {
01627       DeleteRecord(c);
01628     }
01629 
01630   if(!Pack()) return false;
01631 
01632   return true;
01633 }
