00366 bool XFILEDFU::ReadAll()
00367 {
00368   if(!xfilebase) return false;
00369 
00370   bool status = false;
00371 
00372   images.DeleteContents();
00373   images.DeleteAll();
00374 
00375   xfilebase->SetPosition(0);  
00376 
00377   //------------------------------------------
00378   // Read Prefix
00379 
00380   status = xfilebase->Read(prefix.signature, XFILEDFU_PREFIX_SIGNATUREPREXIF_MAX);
00381   if(status) status = xfilebase->Read(&prefix.version, 1);
00382   if(status) status = xfilebase->Read((XBYTE*)&prefix.imagesize, sizeof(XDWORD));
00383   if(status) status = xfilebase->Read(&prefix.ntargets, sizeof(XBYTE));
00384 
00385   if(status)
00386     {
00387       for(int c=0; c<prefix.ntargets; c++)
00388         {      
00389           XFILEDFU_IMAGE* image = Read_Image();
00390           if(image)images.Add(image);      
00391         }  
00392     }
00393 
00394   //------------------------------------------
00395   // Read Suffix
00396 
00397   status =xfilebase->Read((XBYTE*)&suffix.BCDdevice, sizeof(XWORD));
00398   if(status) status = xfilebase->Read((XBYTE*)&suffix.IDproduct, sizeof(XWORD));
00399   if(status) status = xfilebase->Read((XBYTE*)&suffix.IDvendor, sizeof(XWORD));
00400   if(status) status = xfilebase->Read((XBYTE*)&suffix.BCDDFU, sizeof(XWORD));
00401   if(status) status = xfilebase->Read((XBYTE*)&suffix.signature, XFILEDFU_PREFIX_SIGNATURESUFFIX_MAX);
00402   if(status) status = xfilebase->Read(&suffix.length, 1);
00403   if(status) status = xfilebase->Read((XBYTE*)&suffix.CRC, sizeof(XDWORD));
00404 
00405 
00406   //--------------------------------------------
00407   // Check CRC used in DFU.
00408   
00409   status = CheckCRC();
00410    
00411   return status;
00412 }
