00487 XFILEDFU_IMAGE* XFILEDFU::Read_Image()
00488 {
00489   XFILEDFU_IMAGE* image = new XFILEDFU_IMAGE();
00490   if(!image) return NULL;
00491 
00492   bool status = false;
00493 
00494   status = xfilebase->Read(image->signature, XFILEDFU_PREFIX_SIGNATUREIMAGE_MAX);
00495   if(status) xfilebase->Read(&image->alternatesetting, 1);
00496   if(status) xfilebase->Read((XBYTE*)&image->targetnameexist, sizeof(XDWORD));
00497   if(status) xfilebase->Read((XBYTE*)&image->targetname, XFILEDFU_IMAGEPREFIX_NAME_MAX);
00498   if(status) xfilebase->Read((XBYTE*)&image->targetsize, sizeof(XDWORD));
00499   if(status) xfilebase->Read((XBYTE*)&image->nelements, sizeof(XDWORD));
00500 
00501   for(XDWORD c=0; c<image->nelements; c++)
00502     {
00503       XFILEDFU_ELEMENT* element = Read_Element();
00504       if(element) image->elements.Add(element);       
00505     }
00506 
00507   if(!status) 
00508     { 
00509       delete image;
00510       return NULL;
00511     }
00512 
00513   return image;
00514 }
