00526 XFILEDFU_ELEMENT* XFILEDFU::Read_Element()
00527 {
00528   XFILEDFU_ELEMENT* element = new XFILEDFU_ELEMENT();
00529   if(!element) return NULL;
00530 
00531   bool status = false;
00532 
00533   status = xfilebase->Read((XBYTE*)&element->address, sizeof(XDWORD));
00534   if(status) xfilebase->Read((XBYTE*)&element->size, sizeof(XDWORD));
00535   if(status)
00536     {
00537       status = false;
00538       if(element->size)
00539         {
00540           status = element->data.Resize(element->size);
00541           if(status) status = xfilebase->Read(element->data.Get(), element->data.GetSize());            
00542         }
00543     }
00544 
00545   if(!status) 
00546     { 
00547       delete element;
00548       return NULL;
00549     }
00550 
00551   return element;
00552 }
