00564 bool XFILEDFU::CheckCRC()
00565 {
00566   XBUFFER buffer;
00567   bool    status;
00568 
00569   xfilebase->SetPosition(0);
00570   buffer.Resize((XDWORD)(xfilebase->GetSize() - sizeof(XDWORD)));
00571       
00572   status = xfilebase->Read(buffer.Get(), buffer.GetSize());
00573   if(status)
00574     {
00575       XDWORD CRC = 0xFFFFFFFF;
00576         for (XDWORD i = 0; i < buffer.GetSize(); i++) 
00577         {
00578               CRC = CRCtable[(CRC ^ buffer.Get()[i]) & 0xff] ^ (CRC >> 8);
00579             }
00580 
00581       if(CRC != suffix.CRC) status = false;                         
00582     }
00583 
00584   return status;
00585 }
