01493 bool XFILEINI::ConvertFromLines()
01494 {
01495   DeleteAllSections();
01496 
01497   XSTRING   section;
01498   XSTRING   key;  
01499   XSTRING   value;
01500   
01501   for(int c=0;c<GetNLines();c++)
01502     {
01503       XSTRING* line = GetLine(c);
01504       if(line)
01505         {          
01506           XFILEINIREMARK* remark =  new XFILEINIREMARK();   
01507           if(remark)
01508             {
01509               if(!line->HaveCharacters())
01510                 {
01511                   section.Empty();                  
01512                   key.Empty(); 
01513                   value.Empty();
01514                 }
01515                else
01516                 {
01517                   Line_GetRemark((*line), (*remark)); 
01518                   
01519                   XSTRING _section;
01520                   if(Line_IsSection((*line), _section))
01521                     {
01522                       section = _section;
01523 
01524                       if(!remark->GetTextRemark()->IsEmpty()) remark->SetType(XFILEINI_TYPEREMARK_IN_SECTION);
01525                       
01526                       CreateSection(section);
01527 
01528                       int index = 1;  
01529 
01530                       for(XDWORD c=0; c<remarks.GetSize(); c++)
01531                         {
01532                           XFILEINIREMARK* tmp_remark = remarks.Get(c);
01533                           if(tmp_remark)
01534                             {
01535                               if(tmp_remark->GetType() == XFILEINI_TYPEREMARK_ALL_LINE)
01536                                 {
01537                                   if(tmp_remark->GetRelativeSection()->IsEmpty())  
01538                                     {
01539                                       tmp_remark->GetRelativeSection()->Set(section); 
01540                                       tmp_remark->GetRelativeKey()->Set(key);
01541                                       tmp_remark->SetRelativeYPos(index);
01542                                       index++;
01543                                     }
01544                                 } 
01545                             }                      
01546                         }
01547                     }
01548                    else
01549                     {            
01550                       XSTRING _key;
01551   
01552                       if(Line_IsKey((*line), _key, value))
01553                         {
01554                           key = _key;
01555 
01556                           if(!remark->GetTextRemark()->IsEmpty())
01557                             {
01558                               int index_found = value.Find(remark->GetTextRemark()->Get(), true, 0);
01559                               if(index_found != XSTRING_NOTFOUND) 
01560                                 {
01561                                   value.DeleteCharactersToEnd(index_found-1);   
01562 
01563                                   value.DeleteCharacter(__C(' '),  XSTRINGCONTEXT_FROM_FIRST);    
01564                                   value.DeleteCharacter(__C(' '),  XSTRINGCONTEXT_TO_END);
01565                                 }
01566                             }
01567                           
01568                           if(!remark->GetTextRemark()->IsEmpty()) remark->SetType(XFILEINI_TYPEREMARK_IN_KEY);
01569 
01570                           int index = 1; 
01571                           for(XDWORD c=0; c<remarks.GetSize(); c++)
01572                             {
01573                               XFILEINIREMARK* tmp_remark = remarks.Get(c);
01574                               if(tmp_remark)
01575                                 {
01576                                   if(tmp_remark->GetType() == XFILEINI_TYPEREMARK_ALL_LINE)
01577                                     {
01578                                       if(tmp_remark->GetRelativeSection()->IsEmpty())  
01579                                         {
01580                                           tmp_remark->GetRelativeSection()->Set(section); 
01581                                           tmp_remark->GetRelativeKey()->Set(key);
01582                                           tmp_remark->SetRelativeYPos(index);
01583                                           index++;
01584                                         }
01585                                     } 
01586                                 }                      
01587                             }
01588 
01589                           WriteValue(section, key, value);
01590                         }
01591                     }
01592                 }
01593 
01594               switch(remark->GetType())
01595                 {
01596                   case XFILEINI_TYPEREMARK_UNKNOWN     : delete remark;   
01597                                                          break; 
01598 
01599                   case XFILEINI_TYPEREMARK_ALL_LINE    : remarks.Add(remark);
01600                                                          break; 
01601 
01602                   case XFILEINI_TYPEREMARK_IN_SECTION  : remark->GetRelativeSection()->Set(section);
01603                                                          remark->GetRelativeKey()->Empty();
01604                                                          remarks.Add(remark);
01605                                                          break; 
01606 
01607                   case XFILEINI_TYPEREMARK_IN_KEY      : remark->GetRelativeSection()->Set(section);
01608                                                          remark->GetRelativeKey()->Set(key);
01609                                                          remarks.Add(remark);
01610                                                          break; 
01611                 }             
01612             }
01613         }
01614     }
01615 
01616   return true;
01617 }
01618 
01619 
01629 bool XFILEINI::ConvertToLines()
01630 {
01631   if(sections.IsEmpty()) return false;
01632 
01633   for(XDWORD c=0;c<sections.GetSize();c++)
01634     {
01635       XFILEINISECTION* section = (XFILEINISECTION*)sections.Get(c);
01636       if(section)
01637         { 
01638           XSTRING line(__L(" "));
01639           XSTRING text_remark;
01640           XSTRING key;
01641           XSTRING value;
01642 
01643           AddLine(line.Get());
01644          
01645           for(XDWORD d=0; d<remarks.GetSize(); d++)
01646             {
01647               XFILEINIREMARK* remark = remarks.Get(d);
01648               if(remark)
01649                 { 
01650                   line.Empty();
01651   
01652                   if(remark->GetType() == XFILEINI_TYPEREMARK_ALL_LINE)
01653                     {
01654                       if((!remark->GetRelativeSection()->Compare(section->GetName()->Get(), true))  && (remark->GetRelativeKey()->IsEmpty()))
01655                         {
01656                           for(XDWORD e=0; e<remark->GetXPos(); e++)
01657                             {
01658                               line.Add(__L(" "));
01659                             }
01660 
01661                           remark->GetTextRemark()->DeleteCharacter(__C(' '),  XSTRINGCONTEXT_FROM_FIRST);    
01662                           remark->GetTextRemark()->DeleteCharacter(__C(' '),  XSTRINGCONTEXT_TO_END);
01663                           
01664                           line.AddFormat(__L("; %s"), remark->GetTextRemark()->Get());
01665                           AddLine(line.Get());
01666                         }    
01667                     }                     
01668                 }  
01669             }            
01670        
01671           line.Format(__L("[%s]"), section->GetName()->Get());
01672 
01673           for(XDWORD d=0; d<remarks.GetSize(); d++)
01674             {
01675               XFILEINIREMARK* remark = remarks.Get(d);
01676               if(remark)
01677                 { 
01678                   if(remark->GetType() == XFILEINI_TYPEREMARK_IN_SECTION)
01679                     {
01680                       if(!remark->GetRelativeSection()->Compare(section->GetName()->Get(), true))
01681                         {
01682                           XDWORD inisizeline = line.GetSize();
01683 
01684                           for(XDWORD e=inisizeline; e<remark->GetXPos(); e++)
01685                             {
01686                               line.Add(__L(" "));
01687                             }
01688 
01689                           remark->GetTextRemark()->DeleteCharacter(__C(' '),  XSTRINGCONTEXT_FROM_FIRST);    
01690                           remark->GetTextRemark()->DeleteCharacter(__C(' '),  XSTRINGCONTEXT_TO_END);
01691 
01692                           line.AddFormat(__L("; %s"), remark->GetTextRemark()->Get());
01693 
01694                           break;
01695                         }    
01696                     }                     
01697                 }  
01698             }           
01699           
01700           AddLine(line.Get());
01701 
01702           section->ResetSelectionKey();
01703 
01704           while(section->GetNextKey(key, value))
01705             {
01706               for(XDWORD d=0; d<remarks.GetSize(); d++)
01707                 {
01708                   XFILEINIREMARK* remark = remarks.Get(d);
01709                   if(remark)
01710                     { 
01711                       line.Empty();
01712 
01713                       if(remark->GetType() == XFILEINI_TYPEREMARK_ALL_LINE)
01714                         {
01715                           if((!remark->GetRelativeSection()->Compare(section->GetName()->Get(), true)) && (!remark->GetRelativeKey()->Compare(key.Get(), true)))
01716                             {
01717                               for(XDWORD e=0; e<remark->GetXPos(); e++)
01718                                 {
01719                                   line.Add(__L(" "));
01720                                 }
01721 
01722                               remark->GetTextRemark()->DeleteCharacter(__C(' '),  XSTRINGCONTEXT_FROM_FIRST);    
01723                               remark->GetTextRemark()->DeleteCharacter(__C(' '),  XSTRINGCONTEXT_TO_END);
01724 
01725                               line.AddFormat(__L("; %s"), remark->GetTextRemark()->Get());  
01726                               AddLine(line.Get());                     
01727                             }    
01728                         }                     
01729                     }  
01730                 }           
01731 
01732               line.Format(__L("%s=%s"), key.Get(), value.Get());
01733 
01734               for(XDWORD d=0; d<remarks.GetSize(); d++)
01735                 {
01736                   XFILEINIREMARK* remark = remarks.Get(d);
01737                   if(remark)
01738                     { 
01739                       if(remark->GetType() == XFILEINI_TYPEREMARK_IN_KEY)
01740                         {
01741                           if((!remark->GetRelativeSection()->Compare(section->GetName()->Get(), true)) && (!remark->GetRelativeKey()->Compare(key.Get(), true))) 
01742                             {
01743                               XDWORD inisizeline = line.GetSize();
01744 
01745                               for(XDWORD e=inisizeline; e<remark->GetXPos(); e++)
01746                                 {
01747                                   line.Add(__L(" "));
01748                                 }
01749 
01750                               remark->GetTextRemark()->DeleteCharacter(__C(' '),  XSTRINGCONTEXT_FROM_FIRST);    
01751                               remark->GetTextRemark()->DeleteCharacter(__C(' '),  XSTRINGCONTEXT_TO_END);
01752 
01753                               line.AddFormat(__L("; %s"), remark->GetTextRemark()->Get());
01754 
01755                               break;
01756                             }    
01757                         }                     
01758                     }  
01759                 }           
01760 
01761               AddLine(line.Get());
01762             }
01763         }
01764     }
01765 
01766   return true;
01767 }
01768 
01769 
01781 bool XFILEINI::Serialization(bool read)
01782 {
01783   bool status;
01784 
01785   file->SetPosition(XFILE_SEEKINI);
01786 
01787   DeleteAllLines();
01788 
01789   if(read)
01790     {
01791       ReadAllFile();
01792       status = ConvertFromLines();
01793     }
01794    else
01795     {
01796       status = ConvertToLines();
01797       if(status)
01798         {
01799           WriteAllFile();
01800 
01801           XQWORD pos = 0;
01802           file->GetPosition(pos);
01803 
01804           if(pos) file->SetSize(pos);
01805         }
01806     }
01807 
01808   DeleteAllLines();
01809 
01810   return status;
01811 }
