01883 bool XFILEINI::Line_IsSection(XSTRING& line, XSTRING& section)
01884 {
01885   section.Empty();
01886 
01887   if(line.IsEmpty()) return false;
01888 
01889   line.Copy(__L("["),__L("]"),true,0,section);  
01890   if(section.IsEmpty()) return false;
01891  
01892   section.ToUpperCase();
01893   section.DeleteCharacter(__C(' '), XSTRINGCONTEXT_FROM_FIRST);
01894   section.DeleteCharacter(__C(' '), XSTRINGCONTEXT_TO_END);
01895 
01896   return true;
01897 }
01898 
01899 
01913 bool XFILEINI::Line_IsKey(XSTRING& line, XSTRING& key, XSTRING& value)
01914 {
01915   key.Empty();
01916   value.Empty();
01917 
01918   if(line.IsEmpty()) return false;
01919 
01920   if((line.Find(__L("["),true) != XSTRING_NOTFOUND) &&
01921      (line.Find(__L("]"),true) != XSTRING_NOTFOUND))  return false;
01922  
01923   int indexvalue = line.Find(__L("="),true);
01924   if(indexvalue == XSTRING_NOTFOUND) return false;
01925 
01926   line.Copy(0,indexvalue,key);
01927   line.Copy(indexvalue+1,line.GetSize(),value);
01928 
01929   if(key.IsEmpty()) return false;      
01930 
01931   //key.ToUpperCase();
01932   key.DeleteCharacter(__C(' '),XSTRINGCONTEXT_FROM_FIRST);
01933   key.DeleteCharacter(__C(' '),XSTRINGCONTEXT_TO_END);
01934 
01935   value.DeleteCharacter(__C(' '),XSTRINGCONTEXT_FROM_FIRST);
01936   value.DeleteCharacter(__C(' '),XSTRINGCONTEXT_TO_END);
01937 
01938   return true;
01939 }
01940 
01941 
01954 bool XFILEINI::Line_GetRemark(XSTRING& line, XFILEINIREMARK& remark)
01955 {
01956   if(line.IsEmpty()) return false;
01957 
01958   int index = line.Find(__L(";"), true);
01959   if(index == XSTRING_NOTFOUND) return false;
01960  
01961   remark.SetXPos(index);
01962   line.Copy(index+1, (*remark.GetTextRemark()));
01963 
01964   XSTRING first;
01965 
01966   if(index) line.Copy(0, index, first);
01967       
01968   if(!first.HaveCharacters())
01969     {      
01970       remark.SetType(XFILEINI_TYPEREMARK_ALL_LINE);     
01971       remark.SetRelativeYPos(-1);
01972     }
01973 
01974   first.DeleteCharacter(__C(' '), XSTRINGCONTEXT_TO_END);
01975   first.DeleteCharacter(__C(' '), XSTRINGCONTEXT_FROM_FIRST);
01976 
01977   line = first;
01978 
01979   return true;
01980 }
