00927 bool XFILERIFF::WriteListToFile(XFILERIFF_LIST* list, XBYTE* data, XDWORD datasize)  
00928 {
00929   if(!list) return false;
00930 
00931   XDWORD listdata[3]  = { 0, 0, 0 };
00932 //XQWORD position = 0;
00933 
00934   if(!xfilebase->SetPosition(list->GetPositionFileData())) return false;
00935 
00936   listdata[0] = list->GetType();
00937   listdata[1] = list->GetSize();
00938   
00939   if(list->IsTypeList())
00940     {      
00941       listdata[2] = list->GetTypeList();
00942       if(!xfilebase->Write((XBYTE*)listdata, (sizeof(XDWORD)*3))) return false;
00943     }
00944    else
00945     {
00946       listdata[1] = datasize;
00947       listdata[2] = 0;
00948 
00949       if(!xfilebase->Write((XBYTE*)listdata, (sizeof(XDWORD)*2))) return false;
00950 
00951       if(data && datasize)
00952         {
00953           if(!xfilebase->Write(data, datasize)) return false;
00954         }
00955     }
00956 
00957   return true;
00958 }
