01019 XDWORD XFILERIFF::AdjustSizeOfLists(XFILERIFF_LIST_NODE* node)
01020 {
01021   XFILERIFF_LIST* list        = NULL;
01022   XDWORD          currentsize = 0;
01023 
01024   list = node->GetData();  
01025   if(!list)  return 0;
01026 
01027   if(!list->IsTypeList()) return list->GetSize();
01028 
01029   for(int c=0; c<node->GetNChildren(); c++)
01030     {
01031       XFILERIFF_LIST_NODE* subnode = node->GetChild(c);
01032       if(subnode)
01033         {
01034           XFILERIFF_LIST* sublist = subnode->GetData();
01035           if(sublist)
01036             {
01037               if(sublist->IsTypeList())
01038                 {
01039                   currentsize += (AdjustSizeOfLists(subnode) + (sizeof(XDWORD) * 3));
01040                 }
01041                else 
01042                 {
01043                   currentsize += (sublist->GetSize() + (sizeof(XDWORD) * 2)); 
01044                 }        
01045             }
01046         }
01047     }
01048 
01049   list->SetSize(currentsize + sizeof(XDWORD));
01050 
01051   if(!WriteListToFile(list)) return false; 
01052 
01053   return currentsize;
01054 }
