01069 bool XFILERIFF::ConvertDWORDtoString(XDWORD data, XSTRING& string)
01070 {
01071   XCHAR characters[4] = { 0, 0 ,0 ,0 };
01072 
01073   characters[0] = (XCHAR)((data >> 24) & 0x000000FF);
01074   characters[1] = (XCHAR)((data >> 16) & 0x000000FF);
01075   characters[2] = (XCHAR)((data >> 8)  & 0x000000FF);
01076   characters[3] = (XCHAR)((data)       & 0x000000FF);
01077 
01078   string.Format(__L("%C%C%C%C"),  characters[3], characters[2], characters[1], characters[0]);
01079 
01080   string.ToUpperCase();
01081 
01082   return true;
01083 }
