01100 XFILERIFF_LIST* XFILERIFF::GetList(bool search_chunk, XFILERIFF_LIST_NODE* listnode, XDWORD type, XDWORD fathertypelist)
01101 {
01102   if(!listnode) return NULL;
01103 
01104   XFILERIFF_LIST* listriff = listnode->GetData();
01105   if(listriff)
01106     {
01107       if(listriff->IsTypeList() != search_chunk)
01108         {         
01109           if(fathertypelist  && listnode->GetParent())
01110             {
01111               XFILERIFF_LIST_NODE* fatherlistnode = listnode->GetParent();
01112               if(fatherlistnode)
01113                 {
01114                   XFILERIFF_LIST* fatherlistriff = fatherlistnode->GetData();
01115                   if(fatherlistriff)
01116                     {
01117                       if(fatherlistriff->GetTypeList() == fathertypelist)
01118                         {
01119                           if(search_chunk)
01120                             {
01121                               if(listriff->GetType() == type) 
01122                                 {
01123                                   return listriff;
01124                                 }
01125                             }
01126                            else
01127                             {
01128                               if(listriff->GetTypeList() == type) 
01129                                 {
01130                                   return listriff;
01131                                 }
01132                             }           
01133                         }                        
01134                     }
01135                 }
01136             }
01137            else
01138             { 
01139               if(search_chunk)
01140                 {
01141                   if(listriff->GetType() == type) 
01142                     {
01143                       return listriff;
01144                     }
01145                 }
01146                else
01147                 {
01148                   if(listriff->GetTypeList() == type) 
01149                     {
01150                       return listriff;
01151                     }
01152                 }              
01153             }                     
01154         }
01155     }
01156 
01157   for(XDWORD c=0; c<listnode->GetChildren()->GetSize(); c++)
01158     {
01159       XFILERIFF_LIST_NODE* listsubnode = listnode->GetChildren()->Get(c);
01160       if(listsubnode) 
01161         {
01162           XFILERIFF_LIST* sublistriff = GetList(search_chunk, listsubnode, type, fathertypelist);
01163           if(sublistriff)  return sublistriff;  
01164         }
01165     }
01166 
01167   return NULL;
01168 }
