01229                     }
01230                 }
01231                else
01232                 {
01233                   if(listriff->GetTypeList() == type) 
01234                     {
01235                       return listnode;
01236                     }
01237                 }              
01238             }                     
01239         }
01240     }
01241 
01242   for(XDWORD c=0; c<listnode->GetChildren()->GetSize(); c++)
01243     {
01244       XFILERIFF_LIST_NODE* sublistnode = listnode->GetChildren()->Get(c);
01245       if(sublistnode) 
01246         {
01247           XFILERIFF_LIST_NODE* sub2listnode = GetListNode(search_chunk, sublistnode, type, fathertypelist);
01248           if(sub2listnode)  return sub2listnode;  
01249         }
01250     }
01251 
01252   return NULL;
01253 }
