00628 bool XFILETXT::IsBinaryFile()
00629 {  
00630   if(!IsOpen()) return false;
00631   if(!file)     return false;
00632 
00633   bool istextfile = false;
00634   
00635   #define MAX_BUFFER_TEST 1024*5 
00636 
00637   XBUFFER buffer;
00638   XDWORD  size          = MAX_BUFFER_TEST;
00639   XDWORD  controlchars  = 0;
00640 
00641   buffer.Resize(MAX_BUFFER_TEST);
00642   file->Read(buffer.Get(), &size);
00643 
00644   if(size)
00645     {
00646       for(XDWORD c=0; c<size; c++)
00647         {
00648           if(((buffer.Get()[c] >= 0x00) && (buffer.Get()[c] < 0x09)) ||         
00649              ((buffer.Get()[c] >  0x0D) && (buffer.Get()[c] < 0x20)))
00650             {
00651               controlchars++;
00652             }
00653         }
00654 
00655       int percent = (controlchars*100/size);
00656       if(percent < 10) istextfile = true;
00657     }     
00658   
00659   return !istextfile;
00660 }
