00945 bool XFILETXT::ReadNLines(int nlines)
00946 {
00947   if(!file)           return false;
00948   if(!file->IsOpen()) return false;
00949 
00950   DeleteAllLines();
00951 
00952   XDWORD             sizeBOM              = 0;
00953   XFILETXTFORMATCHAR formatchar           = GetFormatCharFromFile(&sizeBOM);
00954   XDWORD             sizebytescharacter   = SizeOfCharacter(formatchar);
00955 
00956   if(this->formatchar==XFILETXTFORMATCHAR_UNKNOWN) this->formatchar = formatchar;
00957 
00958   file->SetPosition(sizeBOM);
00959 
00960   bool    endfile;
00961   XDWORD  br;
00962   XBUFFER dataline(false);
00963 
00964   XBYTE*  readbuffer = new XBYTE[XFILETXT_MAXBUFFER];
00965   if(!readbuffer) return false;
00966 
00967   memset(readbuffer, 0, XFILETXT_MAXBUFFER);
00968 
00969   do{ XDWORD bufferpos = 0;
00970 
00971       br      = XFILETXT_MAXBUFFER;
00972       endfile = !file->Read(readbuffer, &br);
00973       if(!br) break;
00974       
00975       do{ XFILETXTTYPELF  _typeLF   = XFILETXTTYPELF_UNKNOWN;
00976           XDWORD          sizeLF    = 0;
00977           XDWORD          sizeline  = 0;
00978           bool            endline   = GetSizeOfLine(formatchar, &readbuffer[bufferpos], _typeLF, sizeLF, sizeline, (br-bufferpos));
00979 
00980           if(typeLF == XFILETXTTYPELF_UNKNOWN && _typeLF != XFILETXTTYPELF_UNKNOWN) typeLF = _typeLF;
00981 
00982           if(endline)
00983             {
00984               dataline.Add(&readbuffer[bufferpos], sizebytescharacter*sizeline);
00985               AddLine(formatchar, dataline.Get(), (int)(dataline.GetSize()/sizebytescharacter));
00986               dataline.Delete();
00987 
00988               sizeline  *= sizebytescharacter;
00989               sizeline  += (sizeLF*sizebytescharacter);
00990               bufferpos += sizeline;
00991             }
00992            else
00993             {
00994               int lack = (br-bufferpos);
00995               dataline.Add(&readbuffer[bufferpos], lack);
00996               bufferpos += lack;
00997 
00998               if(endfile && (bufferpos == br))
00999                 {
01000                   AddLine(formatchar, dataline.Get(), (int)(lack/sizebytescharacter));
01001                   dataline.Delete();
01002                 }
01003             }
01004 
01005           if(nlines != XFILETXT_ALLLINES)
01006             {
01007               if(lines.GetSize() > (XDWORD)nlines)
01008                 {
01009                   break;
01010                 }
01011             }
01012           
01013         } while(bufferpos < br);
01014 
01015     } while(!endfile);
01016 
01017   delete [] readbuffer;
01018 
01019   return true;
01020 }
