01105       case XFILETXTFORMATCHAR_UNKNOWN     : break;
01106 
01107       case XFILETXTFORMATCHAR_ASCII       :
01108       case XFILETXTFORMATCHAR_UTF8        :
01109                                             string.ConvertFromUTF8((XBYTE*)line, sizeline);
01110                                             break;
01111 
01112       case XFILETXTFORMATCHAR_UTF16_BE    : { XCHAR* unibuffer = new XCHAR[sizeline];
01113                                               XWORD* wline     = (XWORD*)line;
01114 
01115                                               if(unibuffer)
01116                                                 {
01117                                                   for(XDWORD c=0; c<sizeline; c++)
01118                                                     {
01119                                                       wline[c]     = (wline[c]<<8) | (wline[c]>>8);
01120                                                       unibuffer[c] = wline[c];
01121                                                     }
01122 
01123                                                   string.Set(unibuffer,sizeline);
01124 
01125                                                   delete [] unibuffer;
01126                                                 }
01127                                              }
01128                                              break;
01129 
01130       case XFILETXTFORMATCHAR_UTF16_LE   :  {  XCHAR* unibuffer = new XCHAR[sizeline];
01131                                                XWORD*          wline     = (XWORD*)line;
01132                                                if(unibuffer)
01133                                                  {
01134                                                    for(XDWORD c=0; c<sizeline; c++)
01135                                                      {
01136                                                        unibuffer[c] = wline[c];
01137                                                      }
01138 
01139                                                    string.Set(unibuffer,sizeline);
01140 
01141                                                    delete [] unibuffer;
01142                                                  }
01143                                             }
01144                                             break;
01145     }
01146 
01147   return true;
01148 }
