01164 bool XFILETXT::AddLine(XFILETXTFORMATCHAR formatchar, XBYTE* line, XDWORD sizeline)
01165 {
01166   XSTRING* string = new XSTRING();
01167   if(!string) return false;
01168 
01169   if(GenerateLineFromBuffer(formatchar, line, sizeline, (*string)))
01170     {
01171       lines.Add(string);
01172     }
01173    else
01174     {
01175       delete string;
01176       string = NULL;
01177     }
01178 
01179   return string?true:false;
01180 }
