01219 bool XFILETXT::AddLineAlready(XSTRING& line, XDWORD* resultsizeline, XDWORD* resultsizeLF)
01220 {
01221   XBYTE   LF[4]   = { 0,0,0,0 };
01222   XDWORD  sizeLF  = 0;
01223 
01224   CreateTypeLF(formatchar, typeLF, LF, sizeLF);
01225 
01226   file->SetPosition((XDWORD)XFILE_SEEKEND);
01227 
01228   bool status = true;
01229 
01230   XBYTE* buffer = new XBYTE[XFILETXT_MAXBUFFER];
01231   if(buffer)
01232     {
01233       int bw = 0;
01234       memset(buffer,0,XFILETXT_MAXBUFFER);
01235 
01236       switch(formatchar)
01237         {
01238           case XFILETXTFORMATCHAR_UNKNOWN   : break;
01239 
01240           case XFILETXTFORMATCHAR_ASCII     : { bw = line.GetSize();
01241 
01242                                                 XBUFFER charstr;
01243                                                 
01244                                                 line.ConvertToASCII(charstr); 
01245                                                 memcpy(buffer, (XBYTE*)charstr.Get(), bw);                                                
01246                                               }
01247                                               break;
01248 
01249           case XFILETXTFORMATCHAR_UTF8      : line.ConvertToUTF8(buffer,bw);
01250                                               break;
01251 
01252           case XFILETXTFORMATCHAR_UTF16_BE  : { XWORD* bufferw = (XWORD*)buffer;
01253 
01254                                                 bw = (line.GetSize()*2);
01255 
01256                                                 for(XDWORD d=0; d<line.GetSize(); d++)
01257                                                   {
01258                                                     bufferw[d] = (XWORD)line.Get()[d];
01259                                                     bufferw[d] = (bufferw[d]<<8) | (bufferw[d]>>8);
01260                                                   }
01261                                               }
01262                                               break;
01263 
01264           case XFILETXTFORMATCHAR_UTF16_LE  : { XWORD* bufferw = (XWORD*)buffer;
01265 
01266                                                 bw = (line.GetSize()*2);
01267 
01268                                                 for(XDWORD d=0; d<line.GetSize(); d++)
01269                                                   {
01270                                                     bufferw[d] = (XWORD)line.Get()[d];
01271                                                   }
01272                                               }
01273                                               break;
01274         }
01275 
01276       if(bw)
01277         {
01278           if(file->Write(buffer,bw))
01279             {
01280               if(file->Write(LF,sizeLF))
01281                 {
01282                   if(resultsizeline) (*resultsizeline) = bw;
01283                   if(resultsizeLF)   (*resultsizeLF)   = sizeLF;
01284 
01285                 }  else status = false;
01286 
01287             } else status = false;
01288         }
01289 
01290       delete [] buffer;
01291     }
01292 
01293   return status;
01294 }
