01386 
01399 bool XFILETXT::AddBufferLines(XFILETXTFORMATCHAR formatchar, XBUFFER& xbuffer)
01400 {
01401   if(xbuffer.IsEmpty()) return false;
01402 
01403   XBYTE* buffer = xbuffer.Get();
01404   if(!buffer) return false;
01405 
01406   int br    = xbuffer.GetSize();
01407   int nchar = 0;
01408 
01409   this->formatchar = formatchar;
01410 
01411   do{ XFILETXTTYPELF typeLF;
01412       XDWORD         sizeLF   = 0;
01413       XDWORD         sizeline = 0;
01414 
01415       bool endline = GetSizeOfLine(formatchar, &buffer[nchar], typeLF, sizeLF, sizeline, (br-nchar));
01416 
01417       if(!endline) sizeline = (br-nchar);
01418 
01419       if(this->typeLF == XFILETXTTYPELF_UNKNOWN) this->typeLF = typeLF;
01420 
01421       AddLine(formatchar, (XBYTE*) &buffer[nchar], sizeline);
01422 
01423       switch(formatchar)
01424         {
01425           case XFILETXTFORMATCHAR_UNKNOWN   :
01426           case XFILETXTFORMATCHAR_ASCII     :
01427           case XFILETXTFORMATCHAR_UTF8      : nchar+=(sizeline+(endline?sizeLF:0));
01428                                               break;
01429 
01430           case XFILETXTFORMATCHAR_UTF16_BE  :
01431           case XFILETXTFORMATCHAR_UTF16_LE  : nchar+=((sizeline*2)+(endline?(sizeLF*2):0));
01432                                               break;
01433         }
01434 
01435       } while(nchar<br);
01436 
01437   return true;
01438 }
