01440 
01457 bool XFILETXT::GetSizeOfLine(XFILETXTFORMATCHAR formatchar, XBYTE* buffer, XFILETXTTYPELF& typeLF, XDWORD& sizeLF, XDWORD& size, XDWORD maxsize)
01458 {
01459   bool    status = false;
01460   XDWORD  c      = 0;
01461 
01462   sizeLF = 0;
01463 
01464   typeLF = XFILETXTTYPELF_UNKNOWN;
01465 
01466   XWORD* bufw = (XWORD*) buffer;
01467   XBYTE* bufb = (XBYTE*) buffer;
01468 
01469   switch(formatchar)
01470     {
01471       case XFILETXTFORMATCHAR_UNKNOWN   :
01472       case XFILETXTFORMATCHAR_ASCII     :
01473       case XFILETXTFORMATCHAR_UTF8      : for(c=0; c<maxsize; c++)
01474                                             {
01475                                               if(bufb[c]==0x0D)
01476                                                 {
01477                                                   status = true;
01478                                                   sizeLF = 1;
01479                                                   typeLF = XFILETXTTYPELF_0D;
01480                                                   if(bufb[c+1]==0x0A)
01481                                                     {
01482                                                       typeLF = XFILETXTTYPELF_0D0A;
01483                                                       sizeLF++;
01484                                                     }
01485                                                   break;
01486                                                 }
01487 
01488                                               if(bufb[c]==0x0A)
01489                                                 {
01490                                                   status = true;
01491                                                   sizeLF = 1;
01492                                                   typeLF = XFILETXTTYPELF_0A;
01493                                                   if((c+1) < maxsize)
01494                                                     {
01495                                                       if(bufb[c+1]==0x0D)
01496                                                         {
01497                                                           typeLF = XFILETXTTYPELF_0A0D;
01498                                                           sizeLF++;
01499                                                         }
01500                                                     }
01501                                                   break;
01502                                                 }
01503                                             }
01504                                           break;
01505 
01506       case XFILETXTFORMATCHAR_UTF16_LE  : for(c=0;c<maxsize/2;c++)
01507                                             {
01508                                               if(bufw[c]==0x000D)
01509                                                 {
01510                                                   status = true;
01511                                                   sizeLF = 1;
01512                                                   typeLF = XFILETXTTYPELF_0D;
01513                                                   if(bufw[c+1]==0x000A)
01514                                                     {
01515                                                       typeLF = XFILETXTTYPELF_0D0A;
01516                                                       sizeLF++;
01517                                                     }
01518                                                   break;
01519                                                 }
01520 
01521                                               if(bufw[c]==0x000A)
01522                                                 {
01523                                                   status = true;
01524                                                   sizeLF = 1;
01525                                                   typeLF = XFILETXTTYPELF_0A;
01526                                                   if(bufw[c+1]==0x000D)
01527                                                     {
01528                                                       typeLF = XFILETXTTYPELF_0A0D;
01529                                                       sizeLF++;
01530                                                     }
01531                                                   break;
01532                                                 }
01533                                             }
01534                                           break;
01535 
01536       case XFILETXTFORMATCHAR_UTF16_BE  : for(c=0; c<maxsize/2; c++)
01537                                             {
01538                                               if(bufw[c]==0x0D00)
01539                                                 {
01540                                                   status = true;
01541                                                   sizeLF = 1;
01542                                                   typeLF = XFILETXTTYPELF_0D;
01543                                                   if(bufw[c+1]==0x0A00)
01544                                                     {
01545                                                       typeLF = XFILETXTTYPELF_0D0A;
01546                                                       sizeLF++;
01547                                                     }
01548                                                   break;
01549                                                 }
01550 
01551                                               if(bufw[c]==0x0A00)
01552                                                 {
01553                                                   status = true;
01554                                                   sizeLF = 1;
01555                                                   typeLF = XFILETXTTYPELF_0A;
01556                                                   if(bufw[c+1]==0x0D00)
01557                                                     {
01558                                                       typeLF = XFILETXTTYPELF_0A0D;
01559                                                       sizeLF++;
01560                                                     }
01561                                                   break;
01562                                                 }
01563                                             }
01564                                           break;
01565     }
01566 
01567   if(status) size = c;
01568 
01569   return status;
01570 }
