01618 bool XFILETXT::CopyTo(XFILETXT* filetxt)
01619 {
01620   if(!filetxt) return false;
01621 
01622   filetxt->formatchar = formatchar;
01623   filetxt->typeLF     = typeLF;
01624   filetxt->LF         = LF;
01625 
01626   filetxt->lines.DeleteContents();
01627   filetxt->lines.DeleteAll();
01628 
01629   for(int c=0; c<GetNLines(); c++)
01630     {
01631       XSTRING* line  = new XSTRING();
01632       if(line)
01633         {
01634           (*line) = GetLine(c)->Get();
01635           filetxt->lines.Add(line);
01636         }
01637     }
01638 
01639   return true;
01640 }
