01212 bool XFILEXML::DecodeAllLines()
01213 {
01214   DeleteAllElements();
01215 
01216   XSTRING   string;
01217   XSTRING*  stringml;
01218   bool      quote      =  false;
01219   bool      cdata      =  false;
01220   int       c          =  0;
01221 
01222   string.Empty();
01223 
01224   while(c<GetNLines())
01225     {
01226       stringml = GetLine(c);
01227 
01228       for(int d=0;d<(int)stringml->GetSize();d++)
01229         {
01230           XCHAR letter[2] = { 0, 0 };
01231           letter[0] = (*stringml)[d];
01232 
01233           if((*stringml)[d]==__C('\"')) quote=!quote;
01234 
01235           if((*stringml)[d]==__C('<')&&(!quote))
01236             {
01237               if(string.Find(__L("[CDATA["),false)!=XSTRING_NOTFOUND) cdata=true;
01238 
01239               if(!string.IsEmpty() && string.HaveCharacters() && (!cdata))
01240                 {
01241                   DecodeLine(string,false);
01242                   string.Empty();
01243                 }
01244 
01245               quote = false;
01246               string += letter;
01247             }
01248            else
01249             {
01250               if(((*stringml)[d]==__C('>'))&&(!quote))
01251                 {
01252                   if(string.Find(__L("]]"),false)!=XSTRING_NOTFOUND) cdata=false;
01253 
01254                   string += letter;
01255                   if(!string.IsEmpty() && string.HaveCharacters() && (!cdata))
01256                     {
01257                       DecodeLine(string,true);
01258                       string.Empty();
01259                     }
01260                 }
01261                else string += letter;
01262             }
01263         }
01264 
01265       c++;
01266     }
01267 
01268   DeleteAllLines();
01269 
01270   return true;
01271 }
