01604 int XFILEXML::DecodeLine(XSTRING& string,bool iselement)
01605 {
01606   XFILEXMLELEMENTTYPE     elementtype  = XFILEXMLELEMENTTYPE_UNKNOWN;
01607   XFILEXMLELEMENTTYPELINE typeline     = XFILEXMLELEMENTTYPELINE_UNKNOWN;
01608 
01609   if(iselement)
01610     {
01611       XSTRING str = string;
01612 
01613       str.DeleteNoCharacters(XSTRINGCONTEXT_ALLSTRING);
01614 
01615       int iini = str.FindCharacter(__C('<'));
01616       int iend = str.FindCharacter(__C('>'),0,true);
01617 
01618       if(iini!=XSTRING_NOTFOUND) iini++;
01619       if(iend!=XSTRING_NOTFOUND) iend--;
01620 
01621       switch(str[iini])
01622         {
01623           case __C('?') : if(str[iend]==__C('?')) elementtype = XFILEXMLELEMENTTYPE_CONFIG;
01624                           break;
01625 
01626           case __C('/') : elementtype = XFILEXMLELEMENTTYPE_NORMAL;
01627                           typeline    = XFILEXMLELEMENTTYPELINE_END;
01628                           break;
01629 
01630           case __C('!') : if((str[iini+1]==__C('-')) && (str[iini+2]==__C('-')) && (str[iend]  ==__C('-')) && (str[iend-1]==__C('-')))
01631                             elementtype = XFILEXMLELEMENTTYPE_COMMENT;
01632                           else
01633                           if(str.Find(__L("[CDATA["),false)!=XSTRING_NOTFOUND)
01634                             elementtype = XFILEXMLELEMENTTYPE_CDATA;
01635                           else
01636                           {
01637                             if((str[iini+1]==__C('-')) && (str[iini+2]==__C('-')))
01638                               is_multicomment=true;
01639                           }
01640 
01641                           break;
01642 
01643                default  : elementtype  = XFILEXMLELEMENTTYPE_NORMAL;
01644                           typeline     = XFILEXMLELEMENTTYPELINE_START;
01645                           if(str[iend]==__C('/'))  typeline    = XFILEXMLELEMENTTYPELINE_STARTEND;
01646                           break;
01647         }
01648 
01649       switch(elementtype)
01650         {
01651           case XFILEXMLELEMENTTYPE_UNKNOWN    : break;
01652           case XFILEXMLELEMENTTYPE_CONFIG   : DecodeConfig(string);             break;
01653           case XFILEXMLELEMENTTYPE_COMMENT  : DecodeComent(string);             break;
01654           case XFILEXMLELEMENTTYPE_CDATA    : DecodeCDATA(string);              break;
01655           case XFILEXMLELEMENTTYPE_NORMAL   : DecodeElement(string,typeline);   break;
01656         }
01657     }
01658    else
01659     {
01660       if(actualelement)
01661         {
01662           actualelement->SetValue(string);
01663         }
01664     }
01665 
01666 
01667   return 0;
01668 }
