01683 bool XFILEXML::DecodeConfig(XSTRING& string)
01684 {
01685   XSTRING values;
01686 
01687   if(string.Copy(__L("<?xml"),__L("?>"),false,0,values)==XSTRING_NOTFOUND)  return false;
01688 
01689   XSTRING name;
01690   XSTRING value;
01691 
01692   int indexname = 0;
01693 
01694   while(1)
01695     {
01696       indexname = values.Copy(indexname, __L("="),false,name);
01697       if(indexname==XSTRING_NOTFOUND) break;
01698 
01699       name.DeleteNoCharacters(XSTRINGCONTEXT_ALLSTRING);
01700 
01701       indexname = values.Copy(__L("\""),__L("\""),false,indexname,value);
01702       if(indexname==XSTRING_NOTFOUND) break;
01703 
01704       XFILEXMLATTRIBUTE* attribute = new XFILEXMLATTRIBUTE(name,value);
01705       if(!attribute) break;
01706 
01707       cfgattributes.Add(attribute);
01708     }
01709 
01710   return true;
01711 }
