01785 bool XFILEXML::DecodeElement(XSTRING& string, XFILEXMLELEMENTTYPELINE typeline)
01786 {
01787   switch(typeline)
01788     {
01789       case XFILEXMLELEMENTTYPELINE_UNKNOWN    : return false;
01790 
01791       case XFILEXMLELEMENTTYPELINE_START    :
01792       case XFILEXMLELEMENTTYPELINE_STARTEND : { XSTRING values;
01793                                                 XSTRING name;
01794                                                 XSTRING value;
01795 
01796                                                 if(string.Copy(__L("<"),__L(" "),false,0,name)==XSTRING_NOTFOUND) return false;
01797 
01798                                                 //name.Set(string);
01799                                                 name.DeleteCharacter(__C('<'),XSTRINGCONTEXT_ALLSTRING);
01800                                                 name.DeleteCharacter(__C('>'),XSTRINGCONTEXT_ALLSTRING);
01801 
01802                                                 name.DeleteNoCharacters();
01803                                                 name.DeleteNoCharacters(__L("\20"),0, XSTRINGCONTEXT_FROM_FIRST);
01804                                                 name.DeleteNoCharacters(__L("\20"),0, XSTRINGCONTEXT_TO_END);
01805 
01806 
01807                                                 XFILEXMLELEMENT* element;
01808 
01809                                                 if(!root)
01810                                                   {
01811                                                     element = new XFILEXMLELEMENT();
01812                                                     if(!element) return false;
01813                                                     root          = element;
01814                                                     actualelement = root;
01815                                                   }
01816                                                  else element = actualelement->AddElement();
01817 
01818                                                 if(!element) return false;
01819 
01820                                                 element->SetType(XFILEXMLELEMENTTYPE_NORMAL);
01821                                                 element->SetName(name);
01822 
01823                                                 if(string.Copy(name.Get(), (const XCHAR*)NULL, false, 0, values)!=XSTRING_NOTFOUND)
01824                                                   {
01825                                                     int indexname = 0;
01826 
01827                                                     while(1)
01828                                                       {
01829                                                         indexname = values.Copy(indexname, __L("="), false, name);
01830                                                         if(indexname==XSTRING_NOTFOUND) break;
01831 
01832                                                         name.DeleteNoCharacters(XSTRINGCONTEXT_ALLSTRING);
01833 
01834                                                         indexname = values.Copy(__L("\""),__L("\""),false,indexname,value);
01835                                                         if(indexname==XSTRING_NOTFOUND) break;
01836 
01837                                                         element->AddAtribute(name,value);
01838 
01839                                                         name.Empty();
01840                                                         value.Empty();
01841 
01842                                                       }
01843                                                   }
01844 
01845                                                 if(typeline==XFILEXMLELEMENTTYPELINE_START) actualelement = element;
01846 
01847                                               }
01848                                               break;
01849 
01850       case XFILEXMLELEMENTTYPELINE_END      : if(actualelement->GetFather()) actualelement = actualelement->GetFather();
01851                                               break;
01852     }
01853 
01854   return true;
01855 }
