01871 bool XFILEXML::AddTabs(int level,int spacetabs,XSTRING& string)
01872 {
01873   if(spacetabs<=0) return false;
01874 
01875   for(int c=0;c<level;c++)
01876     {
01877       for(int d=0;d<spacetabs;d++)
01878         {
01879           string += __L(" ");
01880         }
01881     }
01882 
01883   return true;
01884 }
