01898 bool XFILEXML::EncodeConfig(XSTRING& string)
01899 {
01900   string.Empty();
01901 
01902   string += __L("<?xml ");
01903 
01904   for(int c=0;c<(int)cfgattributes.GetSize();c++)
01905     {
01906       XFILEXMLATTRIBUTE* attribute = (XFILEXMLATTRIBUTE*)cfgattributes.Get(c);
01907       if(!attribute) break;
01908 
01909       string+=attribute->GetName();
01910       string+=__L("=");
01911       string+=__L("\"");
01912       string+=attribute->GetValue();
01913       string+=__L("\" ");
01914     }
01915 
01916   string += __L("?>");
01917 
01918   return true;
01919 }
