01921 
01934 bool XFILEXML::EncodeElements(bool istabulatedline, XFILEXMLELEMENT* element)
01935 {
01936   if(!element) return false;
01937 
01938   XSTRING string;
01939   XSTRING value  = element->GetValue();
01940   int     c;
01941 
01942   string.Empty();
01943   if(istabulatedline)
01944     {
01945       encodelevel++;
01946       AddTabs(encodelevel,XFILEXML_SPACETABS,string);
01947     }
01948 
01949   switch(element->GetType())
01950     {
01951       case XFILEXMLELEMENTTYPE_UNKNOWN    : break;
01952 
01953       case XFILEXMLELEMENTTYPE_CONFIG   : break;
01954 
01955       case XFILEXMLELEMENTTYPE_COMMENT  : { string += __L("<!--");
01956                                             string += value;
01957                                             string += __L("-->");
01958                                             AddLine(string);
01959                                             //XTRACE_PRINTCOLOR(1,string.Get());
01960                                           }
01961                                           break;
01962 
01963       case XFILEXMLELEMENTTYPE_CDATA    : { string += __L("<![CDATA[");
01964                                             string += value;
01965                                             string += __L("]]>");
01966                                             AddLine(string);
01967                                             //XTRACE_PRINTCOLOR(1,string.Get());
01968                                           }
01969                                           break;
01970 
01971       case XFILEXMLELEMENTTYPE_NORMAL   : { bool isclose = false;
01972 
01973                                             string += __L("<");
01974                                             string += element->GetName();
01975                                             if(element->GetNAttributes()) string += __L(" ");
01976 
01977                                             for(c=0;c<(int)element->GetNAttributes();c++)
01978                                               {
01979                                                 XFILEXMLATTRIBUTE* attribute = (XFILEXMLATTRIBUTE*)element->GetAttribute(c);
01980                                                 if(!attribute) break;
01981 
01982                                                 string+=attribute->GetName();
01983                                                 string+=__L("=");
01984                                                 string+=__L("\"");
01985                                                 string+=attribute->GetValue();
01986                                                 string+=__L("\"");
01987                                                 if(c!=element->GetNAttributes()-1) string+=__L(" ");
01988                                               }
01989 
01990                                             if(!element->GetNElements() && value.IsEmpty())
01991                                               {
01992                                                 string +=__L("/>");
01993                                                 isclose = true;
01994                                               }
01995                                              else string +=__L(">");
01996 
01997                                             AddLine(string);
01998                                             //XTRACE_PRINTCOLOR(1,string.Get());
01999 
02000                                             if(!value.IsEmpty())
02001                                               {
02002                                                 string.Empty();
02003                                                 if(istabulatedline) AddTabs(encodelevel+1,XFILEXML_SPACETABS,string);
02004                                                 string+=value;
02005 
02006                                                 AddLine(string);
02007                                                 //XTRACE_PRINTCOLOR(1,string.Get());
02008                                               }
02009 
02010                                             for(c=0;c<(int)element->GetNElements();c++)
02011                                               {
02012                                                 XFILEXMLELEMENT* subelement = element->GetElement(c);
02013                                                 if(subelement)  EncodeElements(istabulatedline,subelement);
02014                                               }
02015 
02016                                             if(!isclose)
02017                                               {
02018                                                 string.Empty();
02019 
02020                                                 if(istabulatedline) AddTabs(encodelevel,XFILEXML_SPACETABS,string);
02021 
02022                                                 string += __L("</");
02023                                                 string += element->GetName();
02024                                                 string += __L(">");
02025 
02026                                                 AddLine(string);
02027                                                 //XTRACE_PRINTCOLOR(1,string.Get());
02028                                               }
02029                                           }
02030                                           break;
02031 
02032 
02033     }
02034 
02035   if(istabulatedline) encodelevel--;
02036 
02037   return true;
02038 }
