01282 bool XFILEUNZIP::DecompressFile(XSTRING& namesource,XPATH& xpathtarget,XCHAR* nametarget, XCHAR* password)
01283 {
01284   XPATH xpath(namesource);
01285 
01286   XFILECMPZIP* filecmp = GotoFile(xpath);
01287   if(!filecmp) return false;
01288 
01289   bool status = DecompressFile(filecmp,xpathtarget,nametarget,password);
01290 
01291   delete filecmp;
