01311 bool XFILEUNZIP::DecompressFile(XFILECMPZIP* filecmp, XPATH& xpathtarget, XCHAR* nametarget, XCHAR* password)
01312 {
01313   bool status = false;
01314 
01315   if(!filecmp) return false;
01316 
01317   XPATH xpath;
01318 
01319   xpath = xpathtarget;
01320 
01321   if(!nametarget)
01322     {
01323       xpath += filecmp->GetName();
01324     }
01325    else
01326     {
01327       if(!nametarget[0])
01328         {
01329           xpath += filecmp->GetName();
01330 
01331         } else xpath += nametarget;
01332     }
01333 
01334   XSTRING _password(password);
01335 
01336   if(filecmp->Open(_password))
01337     {
01338       XFILE* GEN_XFACTORY_CREATE(xfile, Create_File())
01339       if(xfile)
01340         {
01341           if(xfile->Create(xpath))
01342             {
01343               XBYTE* buffer= new XBYTE[maxsizebuffer];
01344               if(buffer)
01345                 {
01346                   XQWORD totalbytesread = 0;
01347                   int    bytesread      = 0;
01348 
01349                   do {  XFILEZIP_XEVENT xevent(this, XFILEZIP_XEVENT_TYPE_UNZIP_EXTRACTFILE);
01350 
01351                         xevent.GetFileName()->Set(xpathunzip.Get());
01352                         xevent.SetFileCompress(filecmp);
01353 
01354                         bytesread = maxsizebuffer;
01355                         memset(buffer, 0, maxsizebuffer);
01356 
01357                         filecmp->Read(buffer, &bytesread);
01358                         if(bytesread)
01359                           {
01360                             status = xfile->Write(buffer, bytesread);
01361                             if(status)
01362                               {
01363                                 totalbytesread += bytesread;
01364 
01365                                 lasterror            = XFILEZIP_ERROR_NONE;
01366                                 lastoperationpercent = (float)(totalbytesread*100) / filecmp->UnCompressSize();
01367 
01368                                 xevent.SetOperationPercent(lastoperationpercent);
01369 
01370                               } else lasterror = XFILEZIP_ERROR_UNZIP_EXTRACTFILEWRITE;
01371 
01372                            } else lasterror = XFILEZIP_ERROR_UNZIP_EXTRACTFILEREAD;
01373 
01374 
01375                         xevent.SetError(lasterror);
01376 
01377                         PostEvent(&xevent);
01378 
01379                         if(!status) break;
01380 
01381                      } while(bytesread == (int)maxsizebuffer);
01382 
01383                   delete [] buffer;
01384 
01385                   if(filecmp->UnCompressSize() != xfile->GetSize()) status = false;
01386                 }
01387 
01388               xfile->Close();
01389             }
01390 
01391           delete xfile;
01392 
01393         }
01394 
01395       filecmp->Close();
01396     }
01397 
01398   return status;
01399 }
