01411 bool XFILEUNZIP::Close()
01412 {
01413   if(!filehdl) return false;
01414 
01415   bool status = (unzClose(filehdl) == UNZ_OK) ? true : false;
01416 
01417   lastoperationpercent  = status ? 100.0f : 0.0f;
01418   lasterror             = XFILEZIP_ERROR_NONE;
01419 
01420   XFILEZIP_XEVENT xevent(this, XFILEZIP_XEVENT_TYPE_UNZIP_CLOSE);
01421 
01422   xevent.GetFileName()->Set(xpathunzip.Get());
01423   xevent.SetFileCompress(NULL);
01424   xevent.SetError(lasterror);
01425   xevent.SetOperationPercent(lastoperationpercent);
01426 
01427   PostEvent(&xevent);
01428 
01429   filehdl = NULL;
01430 
01431   return true;
01432 }
