01475 {
01476   XFILECMPZIP* filecmp;
01477 
01478   if(!filehdl) return NULL;
01479 
01480   filecmp = new XFILECMPZIP(false,(void*)filehdl);
01481   if(!filecmp) return NULL;
01482 
01483   if(!filecmp->IsActive()) return NULL;
01484 
01485   return filecmp;
